package com.hierynomus.mssmb2.messages

import com.hierynomus.protocol.commons.ByteArrayUtils
import com.hierynomus.smb.SMBBuffer
import spock.lang.Specification

class SMB2ChangeNotifyResponseSpec extends AbstractPacketReadSpec {

  def "should parse notifications"() {
    given:
    String hexString1 = ""
    byte[] bytes1 = ByteArrayUtils.parseHex(hexString1)

    when:
    def resp = convert(bytes1)

    then:
    with (resp as SMB2ChangeNotifyResponse) { r ->
      r.fileNotifyInfoList.size() == 5
      r.fileNotifyInfoList.collect { it.fileName } == ["Img3.png", "Img3.png:AFP_AfpInfo", "Img3.png:com.apple.quarantine", "Img3.png:AFP_AfpInfo", "Img3.png:com.apple.metadataï€¢kMDItemIsScreenCapture"]

    }
  }


  def "should handle STATUS_NOTIFY_CLEANUP"() {
    given:
    String hexString1 = "fe534d42400001000b0100000f000000030000000000000005000000000000000100000000000000610000e063580000000000000000000000000000000000000900480000000000"
    byte[] bytes1 = ByteArrayUtils.parseHex(hexString1)

    when:
    def resp = convert(bytes1)

    then:
    with (resp as SMB2ChangeNotifyResponse) { r ->
      r.fileNotifyInfoList.size() == 0
    }
  }

}
