package com.hierynomus.mssmb2.messages

import com.hierynomus.msfscc.fileinformation.FileIdBothDirectoryInformation
import com.hierynomus.msfscc.fileinformation.FileInformationFactory
import com.hierynomus.smb.SMBBuffer
import spock.lang.Specification

import javax.xml.bind.DatatypeConverter

class SMB2QueryDirectoryResponseSpec extends Specification {

  def "should parse query directory with FileIdBothDirectoryInformation"() {
    given:
    String hexString1 = ""
    byte[] bytes1 = DatatypeConverter.parseHexBinary(hexString1)
    SMB2QueryDirectoryResponse response = new SMB2QueryDirectoryResponse()

    when:
    response.read(new SMBBuffer(bytes1))
    def fileInfoList = FileInformationFactory.parseFileInformationList(response.outputBuffer, FileInformationFactory.getDecoder(FileIdBothDirectoryInformation.class))

    then:
    fileInfoList.size() == 18
    fileInfoList.get(0).fileName == "."
    fileInfoList.get(1).fileName == ".."
    fileInfoList.get(2).fileName == "2"
    fileInfoList.get(8).fileName == "䐀䔀嘀尀樀甀渀椀琀开琀攀猀琀猀洀戀樀愀瀀椀"
  }
}
